import { IoSearch } from "react-icons/io5";
import { LuFlame } from "react-icons/lu";
import { MdKeyboardArrowLeft } from "react-icons/md";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Scrollbar, A11y } from "swiper/modules";
import { useEffect, useRef, useState } from "react";

const searchItems = [
  "موتوربرق",
  "استند آرایشی",
  "کتاب",
  "چراغ شارژری",
  "پاوربانک",
];

const SearchBoxDesktop = () => {
  const [isOpen, setIsOpen] = useState(false);
  const modalRef = useRef(null);

  useEffect(() => {
    function handleClickOutside(event) {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    }

    if (isOpen) {
      document.addEventListener("mousedown", handleClickOutside);
    }

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [isOpen]);

  return (
    <div className="flex grow ml-auto">
      <div className="relative">
        <div className="flex justify-between items-center">
          <div className="grow shrink basis-0">
            <div className="w-[600px] h-[44px] flex items-center bg-[#f0f0f1] rounded-sm grow px-0 lg:p-4">
              <div className="w-full lg:rounded-full rounded-md">
                <div className="flex items-center justify-between grow min-w-0 h-9">
                  <div className="flex cursor-pointer">
                    <IoSearch className="w-[24px] h-[24px] text-gray-400" />
                  </div>
                  <span className="grow px-2 lg:px-4">
                    <div
                      onClick={() => setIsOpen(true)}
                      className="h-full flex items-center text-neutral-500 text-[13px]"
                    >
                      جستجو
                    </div>
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
        {isOpen && (
          <div
            ref={modalRef}
            className="!w-full bg-white flex flex-col grow overflow-y-auto rounded-md absolute left-0 top-0 shadow-modal z-50 border border-[#e0e0e2] ease-in-out max-h-[80vh] scrollbar-none"
          >
            <div className="grow">
              <div className="flex flex-col h-[440px] grow overflow-y-hidden max-w-screen scrollbar-none">
                {/* Search Box Input */}
                <div className="bg-white z-index-1 py-3 lg:py-0 w-[600px] h-[50px]">
                  <div className="flex items-center">
                    <div className="grow">
                      <div
                        id="searchBoxClick"
                        className="flex items-center rounded-sm bg-white mx-4 z-index-3 border-b border-b-[#19bfd3] h-[50px]"
                      >
                        <div className="w-full lg:rounded-full rounded-md">
                          <div className="flex items-center justify-between grow min-w-0 h-9">
                            <span className="grow shadow-modal">
                              <label className="w-full inline-block">
                                <div className="w-full h-[4rem] !px-0 flex items-center">
                                  <div className="grow">
                                    <input
                                      type="text"
                                      className="px-2 w-full h-full focus:outline-none focus:border-none"
                                    />
                                  </div>
                                </div>
                              </label>
                            </span>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
                {/* Search Ressults Modal */}
                <div className="grow flex flex-col pb-4">
                  <div className="lg:px-4">
                    <div className="flex flex-col px-5">
                      <span className="w-full block relative mt-5">
                        <div className="h-full rounded-md leading-0 ">
                          <picture>
                            <img
                              className="rounded-[8px] object-cover w-full h-full inline-block overflow-hidden relative"
                              src="data:image/webp;base64,"
                              alt="search"
                            />
                          </picture>
                        </div>
                      </span>
                    </div>
                  </div>
                  <div className="py-2 hidden-scrollbar">
                    <span className=" cursor-pointer block px-5 lg:px-4 xs:py-2">
                      <div className="flex justify-between items-start">
                        <div className="flex shrink-0 ml-4 mt-0.5">
                          <LuFlame className="w-[25px] h-[25px] text-[#a1a3a8]" />
                        </div>
                        <div className="grow text-right">
                          <span className="grow text-neutral-700 font-Bold leading-8">
                            جستجوهای پرطرفدار
                          </span>
                        </div>
                      </div>
                    </span>
                    <div className="flex w-full">
                      <div className="w-full gap-2 p-2.5">
                        <Swiper spaceBetween={10} slidesPerView="auto">
                          {searchItems.map((item, index) => {
                            return (
                              <SwiperSlide key={index} className="!w-auto">
                                <button className="px-2 py-2 rounded-full border border-[#e0e0e0] bg-white text-neutral-700 font-Bold whitespace-nowrap cursor-pointer flex items-center gap-2">
                                  {item}
                                  <span>
                                    <MdKeyboardArrowLeft className="text-neutral-700 font-Bold" />
                                  </span>
                                </button>
                              </SwiperSlide>
                            );
                          })}
                        </Swiper>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SearchBoxDesktop;
